const Header = () => {
  return (
    <header className="bg-gradient-to-r from-emerald-500 to-blue-950 py-6 px-4 shadow-lg">
      {/* Reintroducimos max-w-5xl y mx-auto para centrar y limitar el ancho del contenido interno */}
      {/* Usamos 'flex justify-between' para que el primer grupo y el último grupo se separen al máximo */}
      <div className="max-w-5xl mx-auto flex justify-between items-center w-full"> 
        {/* Contenedor para los logos y el título - alineado a la izquierda */}
        {/* Usamos un 'gap' más grande en este contenedor si queremos espacio entre el grupo de logos/título y el texto de la derecha */}
        {/* O un 'mr-auto' en este div para que empuje la descripción a la derecha */}
        <div className="flex items-center gap-2"> 
          {/* Logo de Kushki */}
          <img src="KUSHKI.png" alt="Logo de Kushki" className="h-14" />

          {/* Logo de IntelliPrompt (tu logo) */}
          <img src="logoIntelliPrompt.png" alt="Logo de IntelliPrompt" className="h-16" />

          {/* Título IntelliPrompt - quitamos ml-64 y lo dejamos que se posicione naturalmente */}
          <h1 className="text-2xl font-bold text-white ml-4">IntelliPrompt</h1> {/* Ajusta ml-4 para espacio entre logos y título */}
        </div>

        {/* Texto de la descripción - automáticamente a la derecha por justify-between */}
        <div className="text-white opacity-90">
          Crea un prompt y envíalo a la IA de tu preferencia
        </div>
      </div>
    </header>
  );
};

export default Header;
