const Header = () => {
  return (
    <header className="bg-gradient-to-r from-emerald-500 to-blue-950 py-6 px-4 shadow-lg">
      <div className="max-w-5xl mx-auto flex items-center w-full"> {/* quitamos justify-between aquí */}

        {/* Contenedor para los dos logos - Alineado a la izquierda */}
        <div className="flex items-center gap-2 mr-4"> {/* Añadimos mr-4 para un espacio entre logos y el resto del contenido */}
          {/* Logo de Kushki */}
          <img src="KUSHKI.png" alt="Logo de Kushki" className="h-8" />

          {/* Logo de IntelliPrompt */}
          <img src="logoIntelliPrompt.png" alt="Logo de IntelliPrompt" className="h-10" />
        </div>

        {/* El título "IntelliPrompt" se mantiene en su div original, pero ahora ajustaremos su posicionamiento */}
        {/* Usamos flex-grow para que ocupe el espacio central y empuje a la derecha */}
        <h1 className="text-2xl font-bold text-white flex-grow text-center">IntelliPrompt</h1>

        {/* El texto de la descripción a la derecha */}
        <div className="text-white opacity-90 ml-4"> {/* Añadimos ml-4 para separarlo del título */}
          Crea un prompt y envíalo a la IA de tu preferencia
        </div>
      </div>
    </header>
  );
};

export default Header;
