// App.js (para Prompt Generator)
import React, { useState } from 'react';
import HistoryForm from './components/HistoryForm'; // Asegúrate de que la ruta sea correcta
// Importa otros componentes que uses para mostrar el prompt/resultado si los tienes
// import PromptDisplay from './components/PromptDisplay'; // Si tienes un componente para mostrar el prompt

const App = () => {
  const [generatedPrompt, setGeneratedPrompt] = useState(''); // Nuevo estado para el prompt
  const [generatedPromptInfo, setGeneratedPromptInfo] = useState(null); // Para guardar la info adicional si la necesitas

  const handleGeneratePrompt = (prompt, info) => {
    setGeneratedPrompt(prompt); // Guarda el prompt generado
    setGeneratedPromptInfo(info); // Guarda la información adicional (nombre, epicId, etc.)
    // Aquí podrías querer desplazar la vista a donde se muestra el prompt
  };

  // Nueva función para limpiar el prompt generado
  const handleClearGeneratedPrompt = () => {
    setGeneratedPrompt('');
    setGeneratedPromptInfo(null);
  };

  return (
    <div className="min-h-screen bg-gray-50 py-12 px-4 sm:px-6 lg:px-8">
      {/* ... Tu Header (si lo tienes en App.js) ... */}

      <div className="max-w-3xl mx-auto space-y-8">
        <div className="text-center">
          <h1 className="text-3xl font-bold text-gray-900">Generador de Casos de Prueba</h1>
          <p className="mt-2 text-sm text-gray-600">
            Crea un prompt para la IA y genera casos de prueba
          </p>
        </div>

        <HistoryForm 
          onGenerate={handleGeneratePrompt} 
          onClearPrompt={handleClearGeneratedPrompt} // Pasa la nueva función de limpieza
        />

        {/* Aquí es donde mostrarías el prompt generado */}
        {generatedPrompt && (
          <div className="bg-gray-100 p-4 rounded-md overflow-x-auto text-sm">
            <h2 className="font-bold mb-2">Prompt Generado:</h2>
            <pre className="whitespace-pre-wrap">{generatedPrompt}</pre>
            {/* Si quieres mostrar la info adicional: */}
            {/* {generatedPromptInfo && (
              <p className="mt-2 text-gray-600">
                Para: {generatedPromptInfo.name}, Epic: {generatedPromptInfo.epicId}
              </p>
            )} */}
          </div>
        )}
      </div>
    </div>
  );
};

export default App;
