// src/components/ResultDisplay.js
import React from 'react';
import { BiSave, BiCopy } from 'react-icons/bi'; // Asumo que usas react-icons
import { FiSend } from 'react-icons/fi'; // Icono para enviar, puedes elegir otro

const ResultDisplay = ({
  generatedPrompt,
  onSaveStory,
  onSendToAI,         // Nuevo prop para enviar a la IA
  isSendingToAI,      // Nuevo prop para el estado de carga
  aiResponse,         // Nuevo prop para la respuesta de la IA
  aiError             // Nuevo prop para el error de la IA
}) => {
  // Función de copiar (asegúrate de que ya la tienes o añádela)
  const handleCopyClick = () => {
    if (generatedPrompt) {
      navigator.clipboard.writeText(generatedPrompt)
        .then(() => {
          alert('Prompt copiado al portapapeles!');
        })
        .catch(err => {
          console.error('Error al copiar el prompt:', err);
          alert('No se pudo copiar el prompt.');
        });
    }
  };

  return (
    <div className="mt-8 p-6 bg-white rounded-lg shadow-md">
      <h2 className="text-xl font-semibold text-gray-800 mb-4">Prompt generado:</h2>
      {generatedPrompt ? (
        <>
          <div className="bg-gray-100 p-4 rounded-md border border-gray-200 whitespace-pre-wrap text-gray-700">
            {generatedPrompt}
          </div>
          <div className="mt-4 flex flex-wrap gap-3 justify-end">
            <button
              onClick={() => onSaveStory(generatedPrompt)}
              disabled={!generatedPrompt}
              className={`px-4 py-2 rounded-md flex items-center space-x-2 transition duration-200
                ${!generatedPrompt ? 'bg-gray-400 cursor-not-allowed' : 'bg-green-600 hover:bg-green-700 text-white'}`}
            >
              <BiSave className="text-lg" />
              <span>Guardar HU</span>
            </button>
            <button
              onClick={handleCopyClick}
              disabled={!generatedPrompt}
              className={`px-4 py-2 rounded-md flex items-center space-x-2 transition duration-200
                ${!generatedPrompt ? 'bg-gray-400 cursor-not-allowed' : 'bg-blue-600 hover:bg-blue-700 text-white'}`}
            >
              <BiCopy className="text-lg" />
              <span>Copiar</span>
            </button>
            {/* Nuevo botón para enviar a la IA */}
            <button
              onClick={onSendToAI}
              disabled={!generatedPrompt || isSendingToAI}
              className={`px-4 py-2 rounded-md flex items-center space-x-2 transition duration-200
                ${!generatedPrompt || isSendingAI ? 'bg-gray-400 cursor-not-allowed' : 'bg-purple-600 hover:bg-purple-700 text-white'}`}
            >
              {isSendingAI ? (
                <>
                  <svg className="animate-spin -ml-1 mr-2 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                  <span>Enviando...</span>
                </>
              ) : (
                <>
                  <FiSend className="text-lg" />
                  <span>Enviar a IA</span>
                </>
              )}
            </button>
          </div>

          {/* Mostrar respuesta de la IA */}
          {aiError && (
            <div className="mt-4 p-3 bg-red-100 border border-red-400 text-red-700 rounded-md">
              <p className="font-semibold">Error de IA:</p>
              <p>{aiError}</p>
            </div>
          )}

          {aiResponse && (
            <div className="mt-4 p-4 bg-blue-50 border border-blue-200 text-blue-800 rounded-md shadow-sm">
              <h3 className="font-semibold mb-2">Respuesta de la IA:</h3>
              <div className="whitespace-pre-wrap">{aiResponse}</div>
            </div>
          )}
        </>
      ) : (
        <p className="text-gray-500 italic">No hay prompt generado aún.</p>
      )}
    </div>
  );
};

export default ResultDisplay;
