const Header = () => {
  return (
    // Mantienes las clases de Tailwind que ya usabas, pero el CSS las fuerza a los colores de Kushki
    <header className="bg-gradient-to-r from-blue-600 to-indigo-800 py-6 px-4 shadow-lg">
      <div className="max-w-5xl mx-auto flex justify-between items-center">
        {/* El título se queda en blanco, o puedes intentar text-green-500 si la sobreescritura funciona */}
        <h1 className="text-2xl font-bold text-white">IntelliPrompt</h1>
        <div className="text-white opacity-90">
          Crea un prompt y envíalo a la IA de tu preferencia
        </div>
      </div>
    </header>
  );
};

export default Header;
