  return (
    // Cambiamos el gradiente y el color del texto principal
    <header className="bg-kushki-blue py-6 px-4 shadow-lg"> 
      <div className="max-w-5xl mx-auto flex justify-between items-center">
        {/* El título puede ir en el verde de Kushki para destacar o mantener el blanco si prefieres contraste con el azul oscuro */}
        <h1 className="text-2xl font-bold text-kushki-green">IntelliPrompt</h1> 
        <div className="text-white opacity-90">
          Crea un prompt y envíalo a la IA de tu preferencia
        </div>
      </div>
    </header>
  );
};

export default Header;
